    <script>
        $('.collapse').on('show.bs.collapse', function () {
            $('.fa-angle-down').addClass('fa-rotate-180');
        });
        $('.collapse').on('hide.bs.collapse', function () {
            $('.fa-angle-down').removeClass('fa-rotate-180');
        });
    </script>

    <script>
        $(document).ready(function () {
            // console.log(sessionStorage.getItem('type'));
            // console.log($('div[data-id=' + sessionStorage.getItem('type') + ']'))
            if (window.location.href.indexOf("newprofile") > -1) {
                var btn = $('div[data-id=' + sessionStorage.getItem('type') + ']');
                var type = btn.data('id');
                var child = btn.data('child');

                $.ajax({
                    method: "POST",
                    url: '{{ route('profile_switch') }}',
                    data: {
                        '_token': "{{ csrf_token() }}",
                        'type': type,
                        'child': child,
                    },
                    success: data => {
                        $('.blog-components').html(data.view).fadeIn('fast');
                        $('.switch').removeClass('active');
                        btn.addClass('active');
                        sessionStorage.setItem('type', type);
                        // console.log(sessionStorage);

                    },
                    error: () => {
                    }
                })
            }

        })
    </script>
    <script>
        $('.switch').on('click', function (e) {
            var btn = $(e.currentTarget);
            if (btn.hasClass('active') == false)
            var type = btn.data('id');
            var child = btn.data('child');

            $.ajax({
                method: "GET",
                url: '{{ route('profile_switch') }}',
                data: {
                    '_token': "{{ csrf_token() }}",
                    'type': type,
                    'child': child,
                },
                success: data => {
                    $('.blog-components').html(data.view).fadeIn('fast');
                    $('.switch').removeClass('active');
                    btn.addClass('active');
                    sessionStorage.setItem('type',type);
                    console.log(sessionStorage);

                },
                error: () => {
                }
            })
        })
    </script>

